mapper.select:
SELECT 

FROM xq_yw a

WHERE a.xq_id = #{xqId}

SELECT 

FROM xq_yw a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.xq_id = #{xqId}


		AND a.xq_title = #{xqTitle}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM xq_yw a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE xq_yw SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
